var secilen_koltuk_sayisi = 0;


$('.form-horizontal').submit(function(event) {
    event.preventDefault();
        if(text_changed(1) && text_changed(0) &&  $('#tarih').val() != ""){
            var nereden = document.getElementById("nereden").value;
            var nereye = document.getElementById("nereye").value;
            window.location.replace("http://127.0.0.1/otobus/arama?nereden="+nereden+"&"+"nereye="+nereye+"&"+"tarih="+$('#tarih').val());
        }
    else {
            $('#hata').modal('show');
        }
});

function text_changed(which){

    if(which == 1){
        var nereden = document.getElementById("nereden").value;

        if($.inArray(nereden,ilceler) == -1){
            document.getElementById("nereden").value = "NEREDEN";
            return false;
        }
        else {return true;}
    }
    else if(which == 0){
        var nereye = document.getElementById("nereye").value;

        if($.inArray(nereye,ilceler) == -1){
            document.getElementById("nereye").value = "NEREYE";
            return false;
        }
        else {return true;}
    }
    else{
        var gelen = document.getElementById(which).value;
        if(gelen == ""){
            $('#hata').modal('show');
        }
    }

}

function pass_ok(){
    var password = document.getElementById("txtPassword").value;
    var password_confirm = document.getElementById("txtPasswordConfirm").value;

    if(password == password_confirm){
        document.getElementById("alert_pass").innerHTML = "";
        return true;
    }
    else {
        document.getElementById("alert_pass").innerHTML = "<div class='alert alert-danger'><strong>Dikkat : </strong> Şifreler Uyuşmuyor</div>";
        return false;
    }
}

function uye_giris(){
    var uye_email = document.getElementById("email").value;
    var uye_sifre = document.getElementById("password").value;

        if(!uye_email ||  !uye_sifre){
            $('#hata').modal('show');
        }
        else
        {
            var formData = {email:uye_email,sifre:uye_sifre,process:"uye-kontrol"};

            $.ajax({
                url : "http://127.0.0.1/otobus/kernel/verify.php",
                type: "POST",
                dataType: "text",
                data : formData,
                beforeSend: function(){
                    $("#loading").show();
                },
                complete: function(){
                    $("#loading").hide();
                },
                success: function(data, textStatus, jqXHR)
                {
                    if(data == "1"){
                        location.reload();
                    }
                    else {
                        alert(data);
                    }
                },
                error: function (jqXHR, textStatus, errorThrown)
                {
                    alert("Bir hata oluştu lütfen yeniden deneyin.");
                }
            });
        }

}

function sifremi_unuttum(){
    var uye_email = document.getElementById("email-sifremi-unuttum").value;

    if(!uye_email){
        $('#hata').modal('show');
    }
    else
    {
        var formData = {email:uye_email,process:"sifremi_unuttum"};

        $.ajax({
            url : "http://127.0.0.1/otobus/kernel/verify.php",
            type: "POST",
            dataType: "text",
            data : formData,
            beforeSend: function(){
                $("#loading").show();
            },
            complete: function(){
                $("#loading").hide();
            },
            success: function(data, textStatus, jqXHR)
            {
                if(data == "1"){
                    alert("Yeni Şifreniz Mail Adresinize Gönderilmiştir.");
                }
                else {
                    alert(data);
                }
            },
            error: function (jqXHR, textStatus, errorThrown)
            {
                alert("Bir hata oluştu lütfen yeniden deneyin.");
            }
        });
    }

}


function register_click(){
    if(pass_ok()){

        var firstname = document.getElementById("txtFirstName").value;
        var lastname = document.getElementById("txtLastName").value;
        var dogum_tarihi = document.getElementById("tarih").value;
        var password = document.getElementById("txtPassword").value;
        var email = document.getElementById("eposta").value;
        var phone = document.getElementById("phone").value;
        var news;
        var kontrat;
        var sex;

        if(firstname != "" && lastname != "" && password != "" && email != "" && phone != "" && dogum_tarihi != ""){

            if(!$.isNumeric(phone)){
                document.getElementById("alert_pass").innerHTML = "<div class='alert alert-danger'><strong>Dikkat : </strong> Lütfen Telefon Numaranızı Düzeltin.</div>";
                $("html, body").animate({ scrollTop: $('.uyelik-alani').offset().top }, 500);
            }

            if (document.getElementById('cbContract').checked) {
                kontrat = 1;
            }
            else{
                kontrat = 0
            }

            if (document.getElementById('cbNewsletter').checked) {
                news = 1;
            }
            else{
                news = 0
            }

            if (document.getElementById('rdbFemale').checked) {
                sex = 0;
            }
            else{
                sex = 1;
            }

            if(kontrat == 1){

                var formData = {firstname:firstname,lastname:lastname,dogum_tarihi:dogum_tarihi,password:password,email:email,phone:phone,sex:sex,news:news,response:grecaptcha.getResponse(),process:"uyelik"};

                $.ajax({
                    url : "http://127.0.0.1/otobus/kernel/verify.php",
                    type: "POST",
                    dataType: "text",
                    data : formData,
                    beforeSend: function(){
                        $("#loading").show();
                    },
                    complete: function(){
                        $("#loading").hide();
                    },
                    success: function(data, textStatus, jqXHR)
                    {
                        alert(data);
                        if(data == "Üye kaydınız başarıyla oluşturuldu.Lütfen mail adresinizi onaylayınız."){
                            window.location.replace("http://127.0.0.1/otobus/");
                        }
                    },
                    error: function (jqXHR, textStatus, errorThrown)
                    {
                        alert("Bir hata oluştu lütfen yeniden deneyin.");
                    }
                });
            }
            else {

                document.getElementById("alert_pass").innerHTML = "<div class='alert alert-danger'><strong>Dikkat : </strong> Lütfen Sözleşmeyi Okuyup Onaylayınız.</div>";
                $("html, body").animate({ scrollTop: $('.uyelik-alani').offset().top }, 500);
            }
        }

        else {
            document.getElementById("alert_pass").innerHTML = "<div class='alert alert-danger'><strong>Dikkat : </strong> Formu Tam ve Eksiksiz Doldurunuz.</div>";
            $("html, body").animate({ scrollTop: $('.uyelik-alani').offset().top }, 500);
        }

    }
    else{
        $("html, body").animate({ scrollTop: $('.uyelik-alani').offset().top }, 500);
    }

}

